var pages = require('pages')

exports.route = function(arr, res, url)
{
    for(var i in arr)
    {
        if (i == url)
        {
            var meth = 'pages.'+ arr[i] + '('+ res +')';
            if (eval(meth))
            {
                eval(meth);
            }
        }
        else
        {
            res.write('404 error: Page not found');
        }
    }
}
